import {Fighter} from './fighter';
/**
 * Clase que aloja una database con luchadores
 */
export class Fighterdex {
  /**
   * Constructor de la clase
   * @param dataBase Array con los luchadores
   */
  constructor(private dataBase: Fighter[]) {};
  /**
   * Permite añadir luchadores
   * @param luchador Luchador a añadir
   */
  public addFighter(luchador: Fighter) {
    this.dataBase.push(luchador);
  }
  /**
   * Permite eliminar luchador
   * @param luchador Luchador a eliminar
   */
  public delFighter(luchador: Fighter) {
    let pos: number = -1;
    this.dataBase.forEach((iter) => {
      if (iter === luchador) {
        pos = this.dataBase.indexOf(iter);
      }
    });
    if (pos == -1) {
      console.log('Imposible eliminar. Luchador no encontrado');
    } else {
      this.dataBase.splice(pos, 1);
    }
  };
  /**
   * Tamaño de la base de datos
   * @returns size database
   */
  public fighterdexSize(): number {
    return this.dataBase.length;
  }
  /**
   * Permite buscar un luchador
   * @param luchador Luchador a buscar
   * @returns Posicion en la database. Null is no es encontrado
   */
  public buscarLuchador(luchador: Fighter) {
    let pos: number = -1;
    this.dataBase.forEach((iter) => {
      if (iter === luchador) {
        pos = this.dataBase.indexOf(iter);
      }
    });
    if (pos == -1) {
      return null;
    } else {
      return this.dataBase[pos];
    }
  }
};

import {isConvertible} from './isconvertible';
/**
 * Clase que implementa conversiones de fuerza
 */
export class Fuerza implements isConvertible<number> {
  private unidades: [string, number][] = [['newton', 1],
    ['kilopondio', 0.101972]];
  /**
   * Contructor de la clase de conversion de fuerzas
   * @param unidadInicio Unidad en la que se encuentra el valor
   * @param unidadFinal Unidad en la que queremos el valor
   */
  constructor(private readonly unidadInicio: string,
      private readonly unidadFinal: string) {}
  /**
   * Geter UnidadFinal
   * @returns unidadFinal
   */
  getUnidadFinal() {
    return this.unidadFinal;
  }
  /**
   * Getter UnidadInicio
   * @returns unidadInicio
   */
  getUnidadInicio() {
    return this.unidadInicio;
  }
  /**
   * Funcion que convierte entre unidades
   * @param cantidad Valor a convertir
   * @returns Valor ya convertido
   */
  convertir(cantidad: number): number {
    let unidad1 = this.unidades.filter(
        (x) => x[0] === this.unidadInicio);
    let unidad2 = this.unidades.filter(
        (x) => x[0] === this.unidadFinal);
    if ((unidad1.length != 1) || (unidad2.length != 1)) {
      throw new Error('Error detectado con las unidades');
    }
    let resultado = cantidad * (unidad2[0][1]/unidad1[0][1]);
    return +(resultado.toFixed(4));
  }
}

